import { Component } from '@angular/core';
import { PhotoService, UserPhoto } from '../services/photo.service';
import { ActionSheetController } from '@ionic/angular';
import { PhotosService } from '../photoServices/photos.service';
@Component({
  selector: 'app-tab2',
  templateUrl: 'tab2.page.html',
  styleUrls: ['tab2.page.scss'],
  standalone: false,
})
export class Tab2Page {
  nameUser!: string;
  alertButtons = [
    {
      text: "actualizar",
      handler: (data: { Nombre: string; }) => {
        this.setName(data.Nombre);
      }
    }
  ]
  alertInputs = [
    {
      name: "Nombre",
      placeholder: "ingrese el nombre nuevo"
    }
  ]
  constructor(public photoService: PhotoService,
    public actionSheetController: ActionSheetController, public photosService: PhotosService) {}


  // Agregar foto
  addPhotoToGallery() {
    this.photoService.addNewToGallery(this.photosService);
  }

  async ngOnInit() {
    await this.photoService.loadSaved();
    this.photosService.sharedNombre.subscribe(nombreUser => this.nameUser = nombreUser);
    this.photosService.sharedPhoto.subscribe(photo => this.photoService.photos = photo)
  }

  public async showActionSheet(photo: UserPhoto, position: number) {
    const actionSheet = await this.actionSheetController.create({
      header: 'Photos',
      buttons: [{
        text: 'Delete',
        role: 'destructive',
        icon: 'trash',
        handler: () => {
          this.photoService.deletePicture(photo, position, this.photosService);
        }
      }, {
        text: 'Cancel',
        icon: 'close',
        role: 'cancel',
        handler: () => {
          // Nothing to do, action sheet is automatically closed
          }
      }]
    });
    await actionSheet.present();
  }

  setName(name: string){
    this.photosService.nextNombre(name);
  }



}
<ion-header [translucent]="true">
  <ion-toolbar color="primary">
    <ion-title>
      üì∑ Edicion 
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="ion-padding">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">üì∑ Galer√≠a de fotos</ion-title>
    </ion-toolbar>
  </ion-header>

  <div class="photo-grid">
    <div class="photo-item" *ngFor="let photo of photoService.photos; index as position">
      <ion-avatar>
        <ion-img [src]="photo.webviewPath" (click)="showActionSheet(photo, position)"></ion-img>
      </ion-avatar>
    </div>
  </div>

  <ion-card class="ion-text-center">
    <ion-card-content>
      <ion-text color="primary">
        <h1>{{ nameUser }}</h1>
      </ion-text>
    </ion-card-content>
  </ion-card>

  <div class="button-container">
    <ion-button expand="block" (click)="addPhotoToGallery()">üì∏ Tomar Foto</ion-button>
    <ion-button expand="block" color="medium" id="changeName">‚ÑπÔ∏è Cambiar Nombre</ion-button>
  </div>

  <ion-alert
    trigger="changeName"
    header="Actualizar Nombre"
    [buttons]="alertButtons"
    [inputs]="alertInputs">
  </ion-alert>
</ion-content>

<style>
  .photo-grid {
    display: flex;
    flex-wrap: wrap;
    gap: 10px;
    justify-content: center;
  }
  .photo-item ion-avatar {
    width: 100px;
    height: 100px;
  }
  .button-container {
    display: flex;
    justify-content: center;
    gap: 10px;
    margin-top: 20px;
  }
</style>
